namespace HJ100\EPay;

/**
 * 银行
 * Class Bank
 * @package HJ100\EPay
 */
class Bank
{
    /**
     * 所有支持的银行
     */
    const BANKS=array(
        'ICBC'=>array('工商银行','data:image/png;base64,'),
        'ABC'=>array('农业银行','data:image/png;base64,'),
        'BOC'=>array('中国银行','data:image/png;base64,'),
        'CCB'=>array('建设银行','data:image/png;base64,'),
        'CMB'=>array('招商银行','data:image/png;base64,'),
        'BOCM'=>array('交通银行','data:image/png;base64,'),
        'CMBC'=>array('民生银行','data:image/png;base64,'),
        'CNCB'=>array('中信银行','data:image/png;base64,'),
        'CEBB'=>array('光大银行','data:image/png;base64,'),
        'CIB'=>array('兴业银行','data:image/png;base64,'),
        'BOB'=>array('北京银行','data:image/png;base64,'),
        'GDB'=>array('广发银行','data:image/png;base64,'),
        'HXB'=>array('华夏银行','data:image/png;base64,'),
        'PSBC'=>array('邮储银行','data:image/png;base64,'),
        'SPDB'=>array('浦发银行','data:image/png;base64,'),
        'PAB'=>array('平安银行','data:image/png;base64,'),
        'BOS'=>array('上海银行','data:image/png;base64,'),
        'HCCB'=>array('杭州银行','data:image/png;base64,'),
        'ZSBK'=>array('浙商银行','data:image/png;base64,'),
        'QDBK'=>array('青岛银行','data:image/png;base64,'),
        'NBCB'=>array('宁波银行','data:image/png;base64,'),
        'LZBK'=>array('兰州银行','data:image/png;base64,'),
        'NJCB'=>array('南京银行','data:image/png;base64,'),
        'CDCB'=>array('成都银行','data:image/png;base64,'),
        'BJRC'=>array('北京农村商业银行','data:image/png;base64,'),
        'SHRB'=>array('上海农村商业银行','data:image/png;base64,'),
    );

    const EBANKS=array(
        'weixin'=>array('微信',''),
        'alipay'=>array('支付宝',''),
    );

    /**
     * 校验银行代号是否正确
     * @param string $bankCode 银行代号
     * @return bool
     */
    public static function validateBankCode($bankCode){
        if (empty($bankCode)){
            return false;
        }
        $keys=array_keys(self::BANKS);
        $keys=array_merge($keys,array_keys(self::EBANKS));
        return in_array($bankCode,$keys);
    }

    /**
     * 校验银行账号是否正确
     * @param number $bankAccount 银行账号
     * @return bool
     */
    public static function validateBankAccount($bankAccount){
        if (empty($bankAccount)){
            return false;
        }
        if(!is_numeric($bankAccount)){
            return false;
        }
        $length=count($bankAccount);
        if ($length>19||$length<16){
            return false;
        }
        return true;
    }

    /**
     * 获取该银行代码所代表的银行名
     * @param string $bankCode 银行代码
     * @return string 银行名称
     */
    public static function getBankName($bankCode){
        if (array_key_exists($bankCode,self::BANKS)){
            return self::BANKS[$bankCode][0];
        }elseif (array_key_exists($bankCode,self::EBANKS)){
            return self::EBANKS[$bankCode][0];
        }else{
            return '未知';
        }
    }
}